import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import './App.css';
import Header from './components/Header';
import GameContainer from './components/GameContainer';
import SignIn from './components/SignIn';
import SignOut from './components/SignOut';
import GameDetail from './components/GameDetail'; // Create this component for game details
import games from './data/gameData';

function App() {
  const [isSignedIn, setIsSignedIn] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  const filteredGames = games.filter(game =>
    game.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Router>
      <div className="App">
        <Header
          isSignedIn={isSignedIn}
          handleSignIn={() => setIsSignedIn(true)}
          handleSignOut={() => setIsSignedIn(false)}
          searchTerm={searchTerm}
          setSearchTerm={setSearchTerm}
        />

        <Switch>
          <Route path="/signin">
            <SignIn onSignIn={() => setIsSignedIn(true)} />
          </Route>
          <Route path="/signout">
            <SignOut onSignOut={() => setIsSignedIn(false)} />
          </Route>
          <Route path="/games/:gameId">
            <GameDetail games={games} />
          </Route>
          <Route path="/">
            <GameContainer games={filteredGames} />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
