import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../style/Header.css';

const Header = ({ 
  isSignedIn, 
  handleSignIn, 
  handleSignOut, 
  searchTerm, 
  setSearchTerm, 
  genreFilter, 
  setGenreFilter,
  developerFilter, 
  setDeveloperFilter,
  publisherFilter, 
  setPublisherFilter,
  ratingRange, 
  setRatingRange 
}) => {
  const navigate = useNavigate();

  const handleSignInClick = () => {
    navigate('/signin'); // Navigate to the Sign In page
  };

  const handleSignUpClick = () => {
    navigate('/signup'); // Navigate to the Sign Up page
  };

  return (
    <div className="header">
      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="search-bar"
      />
      
      <select value={genreFilter} onChange={(e) => setGenreFilter(e.target.value)}>
        <option value="">All Genres</option>
        {/* Add genre options here */}
        <option value="Action">Action</option>
        <option value="Adventure">Adventure</option>
        <option value="RPG">RPG</option>
        {/* Add more genres as needed */}
      </select>

      <select value={developerFilter} onChange={(e) => setDeveloperFilter(e.target.value)}>
        <option value="">All Developers</option>
        {/* Add developer options here */}
        <option value="Dev Company 1">Dev Company 1</option>
        <option value="Dev Company 2">Dev Company 2</option>
        {/* Add more developers as needed */}
      </select>

      <select value={publisherFilter} onChange={(e) => setPublisherFilter(e.target.value)}>
        <option value="">All Publishers</option>
        {/* Add publisher options here */}
        <option value="Pub Company 1">Pub Company 1</option>
        <option value="Pub Company 2">Pub Company 2</option>
        {/* Add more publishers as needed */}
      </select>

      <input
        type="number"
        placeholder="Min Rating"
        value={ratingRange.min}
        onChange={(e) => setRatingRange({ ...ratingRange, min: e.target.value })}
      />
      <input
        type="number"
        placeholder="Max Rating"
        value={ratingRange.max}
        onChange={(e) => setRatingRange({ ...ratingRange, max: e.target.value })}
      />

      <div className="auth-buttons">
        {isSignedIn ? (
          <button className="btn-signout" onClick={handleSignOut}>Sign Out</button>
        ) : (
          <>
            <button className="btn-signin" onClick={handleSignInClick}>Sign In</button>
            <button className="btn-signup" onClick={handleSignUpClick}>Sign Up</button>
          </>
        )}
      </div>
    </div>
  );
};

export default Header;
