import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../style/Header.css';
import Filter from './Filter';

const Header = ({ 
  isSignedIn, 
  handleSignIn, 
  handleSignOut, 
  genres, 
  tempGenre, 
  setTempGenre, 
  developers, 
  tempDeveloper, 
  setTempDeveloper, 
  publishers, 
  tempPublisher, 
  setTempPublisher, 
  tempRatingRange, 
  setTempRatingRange,
  applyFilters,
  searchTerm,
  setSearchTerm
}) => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false); // State for dark mode
  const navigate = useNavigate();

  const handleSignInClick = () => {
    navigate('/signin');
  };

  const handleSignUpClick = () => {
    navigate('/signup');
  };

  const toggleSidebar = () => {
    setIsSidebarOpen((prev) => !prev);
  };

  const onApplyFilters = () => {
    applyFilters(); // Call applyFilters to update the main states
    setIsSidebarOpen(false); // Close the sidebar after applying filters
  };

  // Toggle dark mode
  const toggleDarkMode = () => {
    setIsDarkMode((prevMode) => !prevMode);
  };

  // Persist dark mode setting in localStorage and apply to the body class
  useEffect(() => {
    // Retrieve the saved dark mode preference from localStorage
    const savedDarkMode = localStorage.getItem('darkMode') === 'true';
    setIsDarkMode(savedDarkMode);
  }, []);

  useEffect(() => {
    // Save dark mode preference to localStorage
    localStorage.setItem('darkMode', isDarkMode);
    
    // Apply dark mode class to body based on the state
    if (isDarkMode) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
  }, [isDarkMode]);

  return (
    <div className="header">
      <button 
        className="btn-menu" 
        onClick={toggleSidebar} 
        aria-label="Menu" 
        aria-expanded={isSidebarOpen} // Added for accessibility
      >
        ‚ò∞ 
      </button>

      <input
        type="text"
        placeholder="Search..."
        className="search-bar"
        aria-label="Search games"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)} // Update search term directly
      />

      <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
        <button className="btn-close" onClick={toggleSidebar} aria-label="Close Menu">‚úñ</button>
        
        <Filter 
          genres={genres}
          tempGenre={tempGenre}
          setTempGenre={setTempGenre}
          developers={developers}
          tempDeveloper={tempDeveloper}
          setTempDeveloper={setTempDeveloper}
          publishers={publishers}
          tempPublisher={tempPublisher}
          setTempPublisher={setTempPublisher}
          tempRatingRange={tempRatingRange}
          setTempRatingRange={setTempRatingRange}
          onApplyFilters={onApplyFilters} // Pass down the function
        />
        
        {/* Dark Mode Toggle Inside Sidebar */}
        <button 
          className="toggle-btn" 
          onClick={toggleDarkMode} 
          aria-label="Toggle Dark Mode"
        >
          {isDarkMode ? 'üåô' : '‚òÄÔ∏è'} Dark Mode
        </button>
      </div>

      <div className="auth-buttons">
        {isSignedIn ? (
          <button className="btn-signout" onClick={handleSignOut}>Sign Out</button>
        ) : (
          <>
            <button className="btn-signin" onClick={handleSignInClick}>Sign In</button>
            <button className="btn-signup" onClick={handleSignUpClick}>Sign Up</button>
          </>
        )}
      </div>
    </div>
  );
};

export default Header;
