import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../style/Header.css';
import Filter from './Filter';

const Header = ({ 
  isSignedIn, 
  handleSignIn, 
  handleSignOut, 
  searchTerm, 
  genres, 
  setGenre, 
  developers, 
  setDeveloper, 
  publishers, 
  setPublisher, 
  ratingRange, 
  setRatingRange,
  applyFilters 
}) => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [tempSearchTerm, setTempSearchTerm] = useState(''); // Temporary search term
  const navigate = useNavigate();

  const handleSignInClick = () => {
    navigate('/signin');
  };

  const handleSignUpClick = () => {
    navigate('/signup');
  };

  const toggleSidebar = () => {
    setIsSidebarOpen((prev) => !prev);
  };

  const onApplyFilters = () => {
    applyFilters(tempSearchTerm); // Call applyFilters with the temporary search term
    setIsSidebarOpen(false); // Close the sidebar after applying filters
  };

  return (
    <div className="header">
      <button className="btn-menu" onClick={toggleSidebar} aria-label="Menu">
        ☰ {/* Menu icon */}
      </button>

      <input
        type="text"
        placeholder="Search..."
        value={tempSearchTerm} // Use the temporary search term
        onChange={(e) => setTempSearchTerm(e.target.value)} // Update the temporary search term
        className="search-bar"
        aria-label="Search games"
      />

      {/* Sidebar for filters */}
      <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
        <button className="btn-close" onClick={toggleSidebar} aria-label="Close Menu">✖</button>
        
        <Filter 
          genres={genres}
          setGenre={setGenre}
          developers={developers}
          setDeveloper={setDeveloper}
          publishers={publishers}
          setPublisher={setPublisher}
          ratingRange={ratingRange}
          setRatingRange={setRatingRange}
          onApplyFilters={onApplyFilters} // Pass down the function
        />
      </div>

      <div className="auth-buttons">
        {isSignedIn ? (
          <button className="btn-signout" onClick={handleSignOut}>Sign Out</button>
        ) : (
          <>
            <button className="btn-signin" onClick={handleSignInClick}>Sign In</button>
            <button className="btn-signup" onClick={handleSignUpClick}>Sign Up</button>
          </>
        )}
      </div>
    </div>
  );
};

export default Header;
