import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../style/GameCard.css';

const GameCard = ({ name, description, image }) => {
  const navigate = useNavigate();

  const handleViewClick = () => {
    // Replace spaces with hyphens and convert to lowercase to make it URL-friendly
    const gameNameSlug = name.toLowerCase().replace(/\s+/g, '-');
    navigate(`/games/${gameNameSlug}`); // Navigate to the game details page with name slug
  };

  const placeholderImage = 'https://via.placeholder.com/150'; // Example placeholder

  return (
    <div className="game-card">
      <img src={image || placeholderImage} alt={name} className="game-image" />
      <h3>{name}</h3>
      <p>{description}</p>
      <button className="btn-view" onClick={handleViewClick}>
        View
      </button>
    </div>
  );
};

export default GameCard;
