import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Filter from './Filter'; // Import the Filter component
import '../style/Header.css';

const Header = ({ 
  isSignedIn, 
  handleSignIn, 
  handleSignOut, 
  searchTerm, 
  setSearchTerm, 
  genres, 
  setGenre, 
  developers, 
  setDeveloper, 
  publishers, 
  setPublisher, 
  ratingRange, 
  setRatingRange 
}) => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const navigate = useNavigate();

  const handleSignInClick = () => {
    navigate('/signin');
  };

  const handleSignUpClick = () => {
    navigate('/signup');
  };

  const toggleSidebar = () => {
    setIsSidebarOpen((prev) => !prev);
  };

  const applyFilters = () => {
    // Logic to apply filters (e.g., fetching results based on selected filters)
    console.log("Applying filters:", {
      genre: genres,
      developer: developers,
      publisher: publishers,
      ratingRange,
    });

    // Hide the sidebar after applying filters
    setIsSidebarOpen(false);
  };

  return (
    <div className="header">
      <button className="btn-menu" onClick={toggleSidebar} aria-label="Menu">
        ☰ {/* Menu icon */}
      </button>

      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="search-bar"
        aria-label="Search games"
      />

      {/* Sidebar for filters */}
      <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
        <button className="btn-close" onClick={toggleSidebar} aria-label="Close Menu">✖</button>
        <Filter 
          genres={genres} 
          setGenre={setGenre} 
          developers={developers} 
          setDeveloper={setDeveloper} 
          publishers={publishers} 
          setPublisher={setPublisher} 
          ratingRange={ratingRange} 
          setRatingRange={setRatingRange} 
          applyFilters={applyFilters} // Pass the applyFilters function
        />
      </div>

      <div className="auth-buttons">
        {isSignedIn ? (
          <button className="btn-signout" onClick={handleSignOut}>Sign Out</button>
        ) : (
          <>
            <button className="btn-signin" onClick={handleSignInClick}>Sign In</button>
            <button className="btn-signup" onClick={handleSignUpClick}>Sign Up</button>
          </>
        )}
      </div>
    </div>
  );
};

export default Header;
