import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../style/GameCard.css';

const GameCard = ({ id }) => {
  const [game, setGame] = useState(null); // State to store the game data
  const [loading, setLoading] = useState(true); // State for loading indicator
  const [error, setError] = useState(null); // State for error handling

  const navigate = useNavigate();

  useEffect(() => {
    const fetchGame = async () => {
      try {
        const response = await fetch(`/api/games/${id}`); // API call to fetch game details by ID
        if (!response.ok) {
          throw new Error('Game not found');
        }
        const data = await response.json();
        setGame(data); // Set the fetched data to state
      } catch (err) {
        setError(err.message); // Handle error if any
      } finally {
        setLoading(false); // Set loading to false after the fetch is complete
      }
    };

    fetchGame(); // Fetch the game details when the component mounts
  }, [id]); // Re-fetch when 'id' changes

  const placeholderImage = 'https://via.placeholder.com/150'; // Example placeholder image

  // Handle loading state
  if (loading) {
    return <div>Loading...</div>;
  }

  // Handle error state
  if (error) {
    return <div>Error: {error}</div>;
  }

  // Handle case when the game is not found
  if (!game) {
    return <div>Game not found</div>;
  }

  // Handle view button click to navigate to game details
  const handleViewClick = () => {
    navigate(`/games/${id}`); // Navigate to the game details page
  };

  return (
    <div className="game-card">
      <img src={game.image || placeholderImage} alt={game.name} className="game-image" />
      <h3>{game.name}</h3>
      <p>{game.description}</p>
      <button className="btn-view" onClick={handleViewClick}>
        View
      </button>
    </div>
  );
};

export default GameCard;
