import React from 'react';

const Filter = ({ 
  genres, 
  tempGenre, 
  setTempGenre, 
  developers, 
  tempDeveloper, 
  setTempDeveloper, 
  publishers, 
  tempPublisher, 
  setTempPublisher, 
  tempRatingRange, 
  setTempRatingRange, 
  onApplyFilters 
}) => {
  return (
    <div>
      <h2>Filters</h2>
      <div>
        <h3>Genre</h3>
        <select value={tempGenre} onChange={(e) => setTempGenre(e.target.value)}>
          <option value="">Select Genre</option>
          {genres.map((genre) => (
            <option key={genre} value={genre}>{genre}</option>
          ))}
        </select>
      </div>
      <div>
        <h3>Developer</h3>
        <select value={tempDeveloper} onChange={(e) => setTempDeveloper(e.target.value)}>
          <option value="">Select Developer</option>
          {developers.map((dev) => (
            <option key={dev} value={dev}>{dev}</option>
          ))}
        </select>
      </div>
      <div>
        <h3>Publisher</h3>
        <select value={tempPublisher} onChange={(e) => setTempPublisher(e.target.value)}>
          <option value="">Select Publisher</option>
          {publishers.map((pub) => (
            <option key={pub} value={pub}>{pub}</option>
          ))}
        </select>
      </div>
      <div>
        <h3>Rating Range</h3>
        <input
          type="number"
          placeholder="Min"
          value={tempRatingRange.min}
          onChange={(e) => setTempRatingRange({ ...tempRatingRange, min: e.target.value })}
        />
        <input
          type="number"
          placeholder="Max"
          value={tempRatingRange.max}
          onChange={(e) => setTempRatingRange({ ...tempRatingRange, max: e.target.value })}
        />
      </div>
      <button onClick={onApplyFilters}>Apply</button>
    </div>
  );
};

export default Filter;
