import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Header from './components/Header';
import GameContainer from './components/GameContainer';
import GameDetail from './components/GameDetail';
import SignIn from './components/SignIn';
import SignUp from './components/SignUp';

function App() {
  const [isSignedIn, setIsSignedIn] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [genre, setGenre] = useState('');
  const [developer, setDeveloper] = useState('');
  const [publisher, setPublisher] = useState('');
  const [ratingRange, setRatingRange] = useState({ min: '', max: '' });

  const genres = ['Action', 'Adventure', 'RPG', 'Strategy'];
  const developers = ['Dev A', 'Dev B', 'Dev C'];
  const publishers = ['Publisher A', 'Publisher B', 'Publisher C'];

  const handleSignIn = () => setIsSignedIn(true);
  const handleSignOut = () => setIsSignedIn(false);

  const applyFilters = () => {
    
    // Log applied filters
    console.log('Filters applied:', {
      searchTerm,
      genre,
      developer,
      publisher,
      ratingRange,
    });
  };

  return (
    <Router>
      <div className="App">
        <HeaderWrapper
          isSignedIn={isSignedIn}
          handleSignIn={handleSignIn}
          handleSignOut={handleSignOut}
          searchTerm={searchTerm} // Directly pass searchTerm
          setSearchTerm={setSearchTerm} // Pass setter for searchTerm
          genres={genres}
          genre={genre} // Pass genre
          setGenre={setGenre} // Pass setter for genre
          developers={developers}
          developer={developer} // Pass developer
          setDeveloper={setDeveloper} // Pass setter for developer
          publishers={publishers}
          publisher={publisher} // Pass publisher
          setPublisher={setPublisher} // Pass setter for publisher
          ratingRange={ratingRange} // Pass ratingRange
          setRatingRange={setRatingRange} // Pass setter for ratingRange
          applyFilters={applyFilters} // Pass applyFilters function
        />

        <Routes>
          <Route path="/" element={<GameContainer 
            searchTerm={searchTerm} 
            genre={genre} 
            developer={developer} 
            publisher={publisher} 
            ratingRange={ratingRange} 
          />} />
          <Route path="/games/:id" element={<GameDetail />} />
          <Route path="/signin" element={<SignIn handleSignIn={handleSignIn} />} />
          <Route path="/signup" element={<SignUp handleSignIn={handleSignIn} />} />
        </Routes>
      </div>
    </Router>
  );
}

// Wrapper for the Header that hides it on the SignIn, SignUp, and GameDetail pages
const HeaderWrapper = ({ 
  isSignedIn, 
  handleSignIn, 
  handleSignOut, 
  searchTerm, 
  setSearchTerm, 
  genres, 
  genre, 
  setGenre, 
  developers, 
  developer, 
  setDeveloper, 
  publishers, 
  publisher, 
  setPublisher, 
  ratingRange, 
  setRatingRange, 
  applyFilters 
}) => {
  const location = useLocation();
  const hideHeaderRoutes = ['/signin', '/signup', '/games/'];
  const isOnGameDetail = location.pathname.startsWith('/games/');
  const showHeader = !hideHeaderRoutes.includes(location.pathname) && !isOnGameDetail;

  return showHeader ? (
    <Header
      isSignedIn={isSignedIn}
      handleSignIn={handleSignIn}
      handleSignOut={handleSignOut}
      searchTerm={searchTerm} // Directly pass searchTerm
      setSearchTerm={setSearchTerm} // Pass setter for searchTerm
      genres={genres}
      genre={genre} // Pass genre
      setGenre={setGenre} // Pass setter for genre
      developers={developers}
      developer={developer} // Pass developer
      setDeveloper={setDeveloper} // Pass setter for developer
      publishers={publishers}
      publisher={publisher} // Pass publisher
      setPublisher={setPublisher} // Pass setter for publisher
      ratingRange={ratingRange} // Pass ratingRange
      setRatingRange={setRatingRange} // Pass setter for ratingRange
      applyFilters={applyFilters} // Pass down the applyFilters function
    />
  ) : null;
};

export default App;
