import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../style/GameDetail.css'; // Import the CSS file

const GameDetail = () => {
  const { id } = useParams();
  const [game, setGame] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGameDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/games/${id}`);
        if (!response.ok) {
          throw new Error(`Failed to fetch game details. Status: ${response.status}`);
        }
        const data = await response.json();
        setGame(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchGameDetails();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!game) {
    return <div>Game not found</div>;
  }

  const placeholderImage = 'https://via.placeholder.com/300';
  const gameImage = game.image ? `http://localhost:5000/images/${game.image}` : placeholderImage;

  return (
    <div className="game-detail">
      <img src={gameImage} alt={game.name} />
      <div className="game-detail-content">
        <h1>{game.name}</h1>
        <p>{game.description}</p>
        <p><strong>Genre:</strong> {game.genre || 'N/A'}</p>
        <p><strong>Release Date:</strong> {game.releaseDate || 'N/A'}</p>
        <p><strong>Rating:</strong> {game.rating || 'N/A'}</p>
        <p><strong>Developer:</strong> {game.developer || 'N/A'}</p>
        <p><strong>Publisher:</strong> {game.publisher || 'N/A'}</p>
      </div>

      {/* Separate div for additional data */}
      <div className="game-additional-info">
        <h2>Additional Information</h2>
        {game.story && (
          <div className="game-additional-section">
            <h3>Story</h3>
            <p>{game.story}</p>
          </div>
        )}
        {game.gameplay && (
          <div className="game-additional-section">
            <h3>Gameplay</h3>
            <p>{game.gameplay}</p>
          </div>
        )}
        {game.main_character && (
          <div className="game-additional-section">
            <h3>Main Character</h3>
            <p>{game.main_character}</p>
          </div>
        )}
        {game.world && (
          <div className="game-additional-section">
            <h3>World</h3>
            <p>{game.world}</p>
          </div>
        )}
        {game.unique_feature && (
          <div className="game-additional-section">
            <h3>Unique Feature</h3>
            <p>{game.unique_feature}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default GameDetail;
