import React, { useState } from 'react';
import SignIn from './SignIn'; // Ensure correct path
import SignOut from './SignOut'; // Ensure correct path

const Header = ({ isSignedIn, handleSignIn, handleSignOut, searchTerm, setSearchTerm }) => {
  const [showSignIn, setShowSignIn] = useState(false);

  return (
    <header>
      <h1>Game Info</h1>
      {isSignedIn ? (
        <div>
          <SignOut onSignOut={handleSignOut} />
          <input
            type="text"
            placeholder="Search games..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      ) : (
        <div>
          {showSignIn ? (
            <SignIn onSignIn={(email) => { handleSignIn(); setShowSignIn(false); }} />
          ) : (
            <button onClick={() => setShowSignIn(true)}>Sign In</button>
          )}
        </div>
      )}
    </header>
  );
};

export default Header;
