import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import "../style/Header.css";

const Header = ({
  isSignedIn,
  handleSignIn,
  handleSignOut,
  genres,
  tempGenre,
  setTempGenre,
  developers,
  tempDeveloper,
  setTempDeveloper,
  publishers,
  tempPublisher,
  setTempPublisher,
  tempRatingRange,
  setTempRatingRange,
  applyFilters,
  searchTerm,
  setSearchTerm,
  gameTitles = [], // Default to an empty array
}) => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(() => {
    const savedDarkMode = localStorage.getItem("darkMode");
    return savedDarkMode === "true";
  });
  const [filteredSuggestions, setFilteredSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [activeSuggestionIndex, setActiveSuggestionIndex] = useState(-1);

  const suggestionListRef = useRef(null); // Create ref for suggestion list

  const navigate = useNavigate();

  const handleSignInClick = () => navigate("/signin");
  const handleSignUpClick = () => navigate("/signup");
  const toggleSidebar = () => setIsSidebarOpen((prev) => !prev);
  const onApplyFilters = () => {
    applyFilters();
    setIsSidebarOpen(false);
  };

  const toggleDarkMode = () => {
    setIsDarkMode((prevMode) => {
      const newDarkMode = !prevMode;
      localStorage.setItem("darkMode", newDarkMode);
      return newDarkMode;
    });
  };

  useEffect(() => {
    if (isDarkMode) {
      document.body.classList.add("dark-mode");
    } else {
      document.body.classList.remove("dark-mode");
    }
  }, [isDarkMode]);

  // Filter suggestions with debounce effect
  useEffect(() => {
    const timeoutId = setTimeout(() => {
      console.log("searchTerm:", searchTerm); // Debug: Check searchTerm
      if (searchTerm) {
        const suggestions = gameTitles.filter((title) =>
          title.toLowerCase().includes(searchTerm.toLowerCase())
        );
        console.log("filteredSuggestions:", suggestions); // Debug: Check filtered suggestions
        setFilteredSuggestions(suggestions);
        setShowSuggestions(suggestions.length > 0); // Show suggestions only if there are any
        setActiveSuggestionIndex(-1); // Reset active suggestion index
      } else {
        setFilteredSuggestions([]);
        setShowSuggestions(false);
      }
    }, 300); // Debounce delay

    return () => clearTimeout(timeoutId);
  }, [searchTerm, gameTitles]);

  const handleSuggestionClick = (suggestion) => {
    setSearchTerm(suggestion);
    setShowSuggestions(false); // Close suggestions
  };

  const handleKeyDown = (e) => {
    if (e.key === "ArrowDown") {
      // Move down, but ensure it doesn't go out of bounds
      setActiveSuggestionIndex((prev) =>
        prev < filteredSuggestions.length - 1 ? prev + 1 : prev
      );
    } else if (e.key === "ArrowUp") {
      // Move up, but ensure it doesn't go out of bounds
      setActiveSuggestionIndex((prev) => (prev > 0 ? prev - 1 : prev));
    } else if (e.key === "Enter") {
      // If an active suggestion is selected, update the search term with it
      if (activeSuggestionIndex >= 0) {
        setSearchTerm(filteredSuggestions[activeSuggestionIndex]);
      }
      // Close the suggestions box
      setShowSuggestions(false);
    } else if (e.key === "Escape") {
      // Close the suggestions box when "Escape" is pressed
      setShowSuggestions(false);
    }
  };

  // Scroll the suggestion list to keep the active suggestion in view
  useEffect(() => {
    if (suggestionListRef.current && filteredSuggestions.length > 0) {
      const activeElement = suggestionListRef.current.children[activeSuggestionIndex];
      if (activeElement) {
        activeElement.scrollIntoView({
          block: "nearest",
          inline: "start",
        });
      }
    }
  }, [activeSuggestionIndex, filteredSuggestions]);

  const renderSuggestion = (suggestion) => {
    const matchIndex = suggestion
      .toLowerCase()
      .indexOf(searchTerm.toLowerCase());
    if (matchIndex === -1) return suggestion;

    const beforeMatch = suggestion.slice(0, matchIndex);
    const matchText = suggestion.slice(
      matchIndex,
      matchIndex + searchTerm.length
    );
    const afterMatch = suggestion.slice(matchIndex + searchTerm.length);

    return (
      <>
        {beforeMatch}
        <span className="highlight">{matchText}</span>
        {afterMatch}
      </>
    );
  };

  return (
    <div className="header">
      <button
        className="btn-menu"
        onClick={toggleSidebar}
        aria-label="Menu"
        aria-expanded={isSidebarOpen}
      >
        â˜°
      </button>

      <div className="search-container">
        <input
          type="text"
          placeholder="Search..."
          className="search-bar"
          aria-label="Search games"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        {showSuggestions && (
          <ul className="autocomplete-suggestions" role="listbox" ref={suggestionListRef}>
            {filteredSuggestions.map((suggestion, index) => (
              <li
                key={index}
                role="option"
                aria-selected={activeSuggestionIndex === index}
                onClick={() => handleSuggestionClick(suggestion)}
                className={`autocomplete-suggestion ${
                  activeSuggestionIndex === index ? "active" : ""
                }`}
              >
                {renderSuggestion(suggestion)}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Sidebar, Filters, and Auth buttons as usual */}
    </div>
  );
};

export default Header;
