import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../style/Header.css';

const Header = ({ isSignedIn, handleSignIn, handleSignOut, searchTerm, setSearchTerm }) => {
  const navigate = useNavigate();

  const handleSignInClick = () => {
    navigate('/signin'); // Navigate to the Sign In page
  };

  const handleSignUpClick = () => {
    navigate('/signup'); // Navigate to the Sign Up page
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      // Optionally handle the search submission
      console.log("Search submitted for:", searchTerm);
      // You can trigger any search functionality here if needed
    }
  };

  return (
    <div className="header">
      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        onKeyPress={handleKeyPress}
        className="search-bar"
      />
      <div className="auth-buttons">
        {isSignedIn ? (
          <button className="btn-signout" onClick={handleSignOut}>Sign Out</button>
        ) : (
          <>
            <button className="btn-signin" onClick={handleSignInClick}>Sign In</button>
            <button className="btn-signup" onClick={handleSignUpClick}>Sign Up</button>
          </>
        )}
      </div>
    </div>
  );
};

export default Header;
