import React from 'react';
import { useParams } from 'react-router-dom';
import games from '../data/gameData'; // Import existing game data
import extraGameData from '../data/addtionalData'; // Import extra game data

const GameDetail = () => {
  const { id } = useParams(); // Get the ID from the URL
  console.log("Current ID:", id); // Log the ID for debugging

  const game = games.find(game => game.id === id); // Find game by ID
  const extraData = extraGameData.find(data => data.id === id); // Find extra data by ID
  
  console.log("Current ID from URL:", id);
console.log("Game found in data:", game);

  if (!game) {
    return <div>Game not found</div>; // This indicates the game was not found in the original data
  }
  
  const fullGameDetails = { ...game, ...extraData }; // Merge data

  return (
    <div className="game-detail">
      <h1>{fullGameDetails.name}</h1>
      <img src={fullGameDetails.image} alt={fullGameDetails.name} />
      <p>{fullGameDetails.description}</p>
      <p><strong>Genre:</strong> {fullGameDetails.genre}</p>
      <p><strong>Release Date:</strong> {fullGameDetails.releaseDate}</p>
      <p><strong>Rating:</strong> {fullGameDetails.rating}</p>
      <p><strong>Developer:</strong> {fullGameDetails.developer}</p>
      <p><strong>Publisher:</strong> {fullGameDetails.publisher}</p>
    </div>
  );
};

export default GameDetail;
