import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const GameDetail = () => {
  const { id } = useParams(); // Get the ID from the URL
  const [game, setGame] = useState(null); // State to store the game data
  const [loading, setLoading] = useState(true); // State for loading indicator
  const [error, setError] = useState(null); // State for handling errors

  // Use useEffect to fetch the game details
  useEffect(() => {
    // Function to fetch the game details from the backend
    const fetchGameDetails = async () => {
      try {
        // Fetching game details from the API using the game ID
        const response = await fetch(`/api/games/${id}`);
        // Check if response is not ok (e.g., 404 or 500)
        if (!response.ok) {
          throw new Error('Game not found');
        }
        // Parse the response as JSON
        const data = await response.json();
        // Set the game data to the state
        setGame(data);
      } catch (err) {
        // Set the error state if something goes wrong
        setError(err.message);
      } finally {
        // Set loading to false once the API call is complete
        setLoading(false);
      }
    };

    // Call the fetch function when the component mounts or when the `id` changes
    fetchGameDetails();
  }, [id]); // Dependency on `id` to refetch when the ID changes

  // If the data is still being loaded, show a loading message
  if (loading) {
    return <div>Loading...</div>;
  }

  // If there is an error, show the error message
  if (error) {
    return <div>Error: {error}</div>;
  }

  // If no game is found, show a not found message
  if (!game) {
    return <div>Game not found</div>;
  }

  // Render the game details once everything is fetched successfully
  return (
    <div className="game-detail">
      <h1>{game.name}</h1>
      {game.image ? <img src={game.image} alt={game.name} /> : <p>No image available</p>}
      <p>{game.description}</p>
      <p><strong>Genre:</strong> {game.genre || 'N/A'}</p>
      <p><strong>Release Date:</strong> {game.releaseDate || 'N/A'}</p>
      <p><strong>Rating:</strong> {game.rating || 'N/A'}</p>
      <p><strong>Developer:</strong> {game.developer || 'N/A'}</p>
      <p><strong>Publisher:</strong> {game.publisher || 'N/A'}</p>
    </div>
  );
};

export default GameDetail;
