import React from 'react';
import '..//Filter.css'; // Create this file for additional styles

const Filter = ({ genres, setGenre, developers, setDeveloper, publishers, setPublisher, ratingRange, setRatingRange }) => {
  return (
    <div className="filter-container">
      <h3 className="filter-title">Filters</h3>

      {/* Filter by Genre */}
      <select onChange={(e) => setGenre(e.target.value)} className="filter">
        <option value="">Select Genre</option>
        {genres.map((genre) => (
          <option key={genre} value={genre}>{genre}</option>
        ))}
      </select>

      {/* Filter by Developer */}
      <select onChange={(e) => setDeveloper(e.target.value)} className="filter">
        <option value="">Select Developer</option>
        {developers.map((developer) => (
          <option key={developer} value={developer}>{developer}</option>
        ))}
      </select>

      {/* Filter by Publisher */}
      <select onChange={(e) => setPublisher(e.target.value)} className="filter">
        <option value="">Select Publisher</option>
        {publishers.map((publisher) => (
          <option key={publisher} value={publisher}>{publisher}</option>
        ))}
      </select>

      {/* Filter by Rating Range */}
      <div className="rating-range">
        <input
          type="number"
          placeholder="Min Rating"
          value={ratingRange.min}
          onChange={(e) => setRatingRange({ ...ratingRange, min: e.target.value })}
          className="filter"
          aria-label="Minimum rating"
        />
        <input
          type="number"
          placeholder="Max Rating"
          value={ratingRange.max}
          onChange={(e) => setRatingRange({ ...ratingRange, max: e.target.value })}
          className="filter"
          aria-label="Maximum rating"
        />
      </div>
    </div>
  );
};

export default Filter;
